const { createAlchemyWeb3 } = require("@alch/alchemy-web3");
const web3 = createAlchemyWeb3(alchemyKey); 

const contractABI = require('../contract-abi.json')
const contractAddress = "0xe3AB1Cd245DcCa8f9A54B0EcF86276271adFD993";
export const contract = new web3.eth.Contract(contractABI, contractAddress);

export const connectWallet = async () => {
    if (window.ethereum) {
      try {
        const addressArray = await window.ethereum.request({
          method: "eth_requestAccounts",
        });
        const obj = {
          status: "ğŸ‘†ğŸ½ Sale is live above.",
          address: addressArray[0],
        };
        return obj;
      } catch (err) {
        return {
          address: "",
          status: "ğŸ˜¥ " + err.message,
        };
      }
    } else {
      return {
        address: "",
        status: (
          <span>
            <p>
              {" "}
              ğŸ¦Š{" "}
              <a target="_blank" rel="noreferrer" href={`https://metamask.io/download.html`}>
                You must install Metamask, a virtual Ethereum wallet, in your
                browser.
              </a>
            </p>
          </span>
        ),
      };
    }
  };

  export const mintPower = async(nftQuantity) => {
    //error handling
    if (nftQuantity === 0) { 
           return {
               success: false,
               status: "â—Please make sure quantity is bigger than 0.",
           }
     }
     if (nftQuantity > 5) { 
      return {
          success: false,
          status: "â—Please make sure quantity is less than or equal to 5.",
      }
}


     //load smart contract
     window.contract = await new web3.eth.Contract(contractABI, contractAddress);

     //set up your Ethereum transaction
 const transactionParameters = {
    to: contractAddress, // Required except during contract publications.
    from: window.ethereum.selectedAddress, // must match user's active address.
    value: parseInt(web3.utils.toWei("0.05","ether") * nftQuantity).toString(16),
    data: window.contract.methods.mintPower(nftQuantity).encodeABI() //make call to NFT smart contract 
};

//sign the transaction via Metamask
try {
const txHash = await window.ethereum
    .request({
        method: 'eth_sendTransaction',
        params: [transactionParameters],
    })
return {
    success: true,
    status: "âœ… Check out your transaction on Etherscan: https://rinkeby.etherscan.io/tx/" + txHash,

}
} catch (error) {
return {
    success: false,
    status: "ğŸ˜¥ Something went wrong: " + error.message
}

}
   }

